import React from 'react';
import './App.css';
import phone from './image/phone_ko.png'
import qrcode from './image/qrcode_global.svg'
import {Container,Row,Col} from "react-bootstrap"
import 'bootstrap/dist/css/bootstrap.min.css';

const App =()=>{
  return (
  <Container className = 'App' fluid>
  <Row>
    <Col sm className="PhoneImg_col">
    <img className="PhoneImg" src ={phone} alt ="휴대폰 화면"></img>
    </Col>
    <Col sm>
    <Row>
    <Col><h1 className="TitleSentence">이상향 테스트</h1></Col>
    </Row>
    <Row>
    <Col><h2 className="ContentSentence">나에게 맞는 향수 찾기</h2></Col>
    </Row>
    <Row>
    <Col><img src ={qrcode} alt ="Qr코드" className="QrPicture"></img></Col>
    <Col><h3 className="QrExplanation">휴대폰으로 QR코드를 스캔 후 최적화된 모바일 환경에서 이용 가능</h3></Col>
    </Row>

    </Col>
  </Row>
</Container>

  );
}

export default App;
