import React from 'react';
import './App.css';
import phone from './image/phone_ko.png'
import qrcode from './image/qrcode_global.svg'

const App =()=>{
  return (
      <div className="App">
      <div className="Appphone">
        <Phone></Phone>
      </div>
      <div className="Apptitle">
        <Title></Title>
        <Content></Content>
        <Qrcode></Qrcode>
      </div>
      </div>

  );
}
const Phone = ()=>{
  return(
    <>
    <div className="Phone">
      <img src ={phone} alt ="휴대폰 화면"></img>
    </div>
    </>
  );
}
const Title = () =>{
  return(
    <>
    <div className="Title">
      <h1>이상향 테스트</h1>
    </div>
    </>
  );
}
const Content = () =>{
  return(
    <>
    <div className="Content">
      <h2 className="contentSentence">나에게 맞는 향수 찾기</h2>
    </div>
    </>
  );
}
const Qrcode = ()=>{
  return(
    <>
    <div className="Qrcode">
     <img src ={qrcode} alt ="Qr코드" className="Qrpicture"></img>
     <h3 className="Qrexplanation">휴대폰으로 QR코드를 스캔 후 최적화된 모바일 환경에서 이용 가능</h3>
    </div>
    </>
  );
}
export default App;
